const requireAuth = require('common/require-basic-auth')

const path = {
	userList: /\/user$/,
	user: /\/user\/(\w+)$/
}

module.exports = options => [{
	request: 'get',
	path: path.userList,
	control: require('common/api/user-list')(options)
},{
	request: 'get',
	path: path.user,
	control: require('common/api/user-get')(options)
},{
	request: 'put',
	path: path.user,
	control: require('common/api/user-create')(options)
},{
	request: 'post',
	path: path.user,
	control: requireAuth(options, require('common/api/user-update')(options))
},{
	request: 'delete',
	path: path.user,
	control: requireAuth(options, require('common/api/user-delete')(options))
}]
